@prefix ff: <https://foerderfunke.org/default#> .

<#Buergergeld> a sh:NodeShape ;
  sh:targetClass ff:Person ;
  sh:property [
    sh:path ff:hasEarningCapacity ;
    sh:datatype xsd:boolean ;
    sh:minCount 1 ;
    sh:rule [
      sh: Triangle ;
      sh: condition [
        sh: and [
          sh: node(ff:hasAge);
          sh: lessThan [
            sh: compare "100"
            ;
            sh: datavalues? (
              ff:hasAge
              )
          ]
        ]
      ]
    ]
  ] ;
  sh:property [
    sh:path ff:hasDisability ;
    sh:datatype xsd:boolean ;
    sh:minCount 1 ;
    sh:rule [
      sh: Triangle ;
      sh: condition [
        sh: and [
          sh: node(ff:hasDisabilityType);
          sh: value (ff:HearingImpairment)
