          sh: or [
            sh: node(ff:hasDisabilityType);
            sh: value (ff:VisualImpairment)
          ]
        ]
      ]
    ]
  ] ;
  sh:property [
    sh:path ff:hasIncome ;
    sh:datatype ff:personalGrossIncome ;
    sh:minCount 1 ;
    sh:rule [
      sh: and [
        sh: node(ff:personalGrossIncome);
