# Define a class for the Kindergeld benefit
ff:Kindergeld a sh:NodeShape ;
    sh:targetClass ff:SocialBenefit ;
    sh:property [
        sh:path ff:hasBenefitUnit ;
        sh:nodeKind ff:Person
    ] .

# Define a class for parents
ff:Parent a sh:NodeShape ;
    sh:targetClass ff:Person ;
    sh:property [
        sh:path ff:hasChild ;
        sh:nodeKind ff:Person
    ] .

# Define a class for children
ff:Child a sh:NodeShape ;
    sh:targetClass ff:Person ;
    sh:property [
        sh:path ff:hasParent ;
        sh:nodeKind ff:Person
    ] .

# Define a class for EU/EEA/Swiss nationals
ff:EU_EEA_Swiss a sh:NodeShape ;
    sh:targetClass ff:Person ;
    sh:property [
        sh:path ff:isNationalOf ;
        sh:nodeKind ff:Country ;
        sh:value ff:Germany ;
        sh:datatype xsd:boolean ;
        sh:maxCount 1
    ] .

# Define a class for parents with unrestricted tax liability
ff:UnrestrictedTaxLiabilityParent a sh:NodeShape ;
