ff:SufficientMeansOfSubsistence a sh:NodeShape ;
    sh:targetClass ff:Person ;
    sh:property [
        sh:path ff:sufficientMeansOfSubsistence ;
        sh:datatype xsd:boolean ;
        sh:value true
    ] .

# Define a class for children with residence in Germany or another EU/EEA/Swiss state
ff:ResidentChild a sh:NodeShape ;
