```turtle
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix ff: <https://foerderfunke.org/default#> .

# METADATA

ff:B100019_LB_576986 a ff:SocialBenefit ;
    rdfs:label "Kindergeld"@de, "Child allowance"@en . 

# CONSTRAINTS

ff:KindergeldShape a sh:NodeShape ;
    ff:checksFundingRequirement ff:B100019_LB_576986 ;
    sh:targetNode ff:User ;
    sh:or (
        ff:TaxPayerShape
        ff:NonTaxPayerShape
    ) ;
    # User must have at least one child for whom they receive child benefit
    sh:property [
        sh:path ff:receivesChildBenefitFor ;
        sh:qualifiedMinCount 1 ;
        sh:qualifiedValueShape ff:ChildShape ;
        sh:message "Does not meet requirement 'User has at least one child that fulfills the criteria.'"@en ;
    ] .

ff:TaxPayerShape a sh:NodeShape ;
    # User must be subject to unlimited income tax liability
    sh:property [
        sh:path ff:incomeTaxLiability ;
        sh:minCount 1 ;
        sh:hasValue ff:UnlimitedTaxLiability ;
    ] ;
    # User must be a citizen of EU, EWR or Switzerland or recognized as refugee
    sh:property [
        sh:path ff:isNationalOf ;
        sh:minCount 1 ;
        sh:not [
            sh:in (ff:EEAplusCH ff:Germany) ;
        ] ;
        sh:message "Nationality is not eligible for child allowance."@en ;
    ] ;
    # If user is not a citizen of EU, EWR or Switzerland, they must have residence permit that allows employment or asylum status
    sh:property [
        sh:path ff:hasResidencePermit ;
        sh:minCount 1 ;
        sh:property [
            sh:path ff:entitlesToEmployment ;
            sh:hasValue true ;
        ] ;
        sh:or [
            sh:path ff:entitledToAsylum ;
            sh:hasValue true ;
        ] ;
    ] ;
    # EU/EEA/Switzerland citizen must live in Germany for at least 4 months to be eligible 
    sh:property [
        sh:path ff:uninterruptedLegalStayForLessThan15Months ;
        sh:minCount 1 ;
        sh:hasValue false ;
    ] ;
    # EU/EEA/Switzerland citizen must fulfill free movement requirements after 4 months of stay in Germany
    sh:property [
        sh:path ff:hasEvent ;
        sh:qualifiedValueShape ff:FreeMovementShape ;
        sh:minCount 1 ;
    ] .

ff:NonTaxPayerShape a sh:NodeShape ;
    # User must have residence permit that allows employment or asylum status
    sh:property [
        sh:path ff:hasResidencePermit ;
        sh:minCount 1 ;
        sh:property [
            sh:path ff:entitlesToEmployment ;
            sh:hasValue true ;
        ] ;
        sh:or [
            sh:path ff:entitledToAsylum ;
            sh:hasValue true ;
        ] ;
    ] .

ff:FreeMovementShape a sh:NodeShape ;
    sh:property [
        sh:path ff:hasDate ;
        sh:minCount 1 ;
        sh:maxInclusive "P4M"^^xsd:duration ;
    ] ;
    # EU/EEA/Switzerland citizen must fulfill free movement requirements after 4 months of stay in Germany
    sh:or (
        ff:EmployedShape
        ff:UnemployedShape
        ff:SelfEmployedShape
        ff:FamilyMemberShape
        ff:SufficientMeansShape
        ff:PermanentResidentShape
    ) .

ff:EmployedShape a sh:NodeShape ;
    sh:property [
        sh:path ff:inEmployment ;
        sh:hasValue true ;
    ] .

ff:UnemployedShape a sh:NodeShape ;
    sh:property [
        sh:path ff:hasEmploymentStatus ;
        sh:hasValue ff:RegisteredUnemployed ;
    ] .

ff:SelfEmployedShape a sh:NodeShape ;
    sh:property [
        sh:path ff:hasEmploymentStatus ;
        sh:hasValue ff:SelfEmployed ;
    ] .

ff:FamilyMemberShape a sh:NodeShape ;
    sh:property [
        sh:path ff:derivesFreedomOfMovementFrom ;
        sh:minCount 1 ;
    ] .

ff:SufficientMeansShape a sh:NodeShape ;
    sh:property [
        sh:path ff:sufficientMeansOfSubsistence ;
        sh:hasValue true ;
    ] ;
    sh:property [
        sh:path ff:adequateHealthInsurance ;
        sh:hasValue true ;
    ] .

ff:PermanentResidentShape a sh:NodeShape ;
    sh:property [
        sh:path ff:permanentRightOfResidence ;
        sh:hasValue true ;
    ] .

ff:ChildShape a sh:NodeShape ;
    # Child must live in EU, EWR or Switzerland
    sh:property [
        sh:path ff:countryOfResidence ;
        sh:minCount 1 ;
        sh:in (ff:EEAplusCH) ;
    ] .
```

