ff:B100019_LB_102716305 a ff:SocialBenefit ;
    rdfs:label "Übergangsgeld für Menschen mit Behinderung"@de, "Transitional allowance for people with disabilities"@en .

# CONSTRAINTS

ff:ÜbergangsgeldShape a sh:NodeShape ;
    ff:checksFundingRequirement ff:B100019_LB_102716305 ;
    sh:targetNode ff:User ;
    # User has a disability and needs special support for integration into working life
	sh:and (
		[
			sh:path ff:hasDisability ;
			sh:minCount 1 ;
			sh:in (true) ;
		]
		[
			sh:path ff:workforceIntegrationProblemBecause ;
			sh:minCount 1 ;
			sh:qualifiedValueShape ff:DisabilityShape ;
		]
	) ;
    # User's rehabilitation provider is the Federal Employment Agency
    sh:property [
        sh:path ff:hasRehabilitationProvider ;
        sh:minCount 1 ;
        sh:in (ff:FederalEmploymentAgency) ;
        sh:message "Does not meet requirement 'User's rehabilitation provider is the Federal Employment Agency'"@en
    ] ;
    # User was socially insured for at least 12 months OR is entitled to unemployment benefit under the SGB III OR is entitled to unemployment assistance under the Soldiers' Pension Act (SVG)
    sh:or (
		[
			sh:path ff:compulsoryInsuranceFor12In30 ;
			sh:minCount 1 ;
			sh:in (true) ;
		]
		[
			sh:path ff:hasEmploymentStatus ;
			sh:qualifiedMinCount 1 ;
            sh:qualifiedValueShape ff:EntitlementShape ;
		]
		[
			sh:path ff:receivesBenefit ;
			sh:qualifiedMinCount 1 ;
            sh:qualifiedValueShape ff:SVGShape ;
		]
	) ;
    # User is a vocational returnee with a disability OR acquired a training certificate from a vocational school or another vocational training institution within the last year before the start of the measure
    sh:or (
    	[
    		sh:path ff:isVocationalReturnee ;
    		sh:minCount 1 ;
    		sh:in (true) ;
    	]
    	[
    		sh:path ff:participates ;
    		sh:qualifiedMinCount 1 ;
            sh:qualifiedValueShape ff:TrainingShape ;
    	]
    ) .

ff:DisabilityShape a sh:NodeShape ;
    # No further constraints .

ff:EntitlementShape a sh:NodeShape ;
    sh:path ff:hasEmploymentStatus ;
    sh:in (ff:RegisteredUnemployed) ;
    sh:property [
        sh:path ff:hasEvent ;
        sh:qualifiedMinCount 1 ;
        sh:qualifiedValueShape ff:EventShape ;
    ] .

ff:EventShape a sh:NodeShape ;
    sh:path ff:hasEvent ;
    sh:property [
        sh:path ff:lessThanFourMonthsAgo ;
        sh:in (true) ;
    ] ;
    sh:property [
        sh:path ff:isRenumerated ;
        sh:in (false) ;
    ] .

ff:SVGShape a sh:NodeShape ;
    sh:path ff:receivesBenefit ;
    sh:in (ff:UnemploymentBenefitsSVG) .

ff:TrainingShape a sh:NodeShape ;
    sh:path ff:participates ;
    sh:property [
        sh:path ff:lessThanFourMonthsAgo ;
        sh:in (false) ;
    ] ;
    sh:property [
        sh:path ff:recognizedByLaw ;
        sh:in ( ff:VocationalTrainingAct ff:MaritimeLabourAct ff:NursingProfessionsAct ff:CraftsCode ) ;
    ] ;
    sh:or (
    	[
    		sh:path ff:isFirstVocationalTraining ;
    		sh:in (true) ;
    	]
    	[
    		sh:path ff:commissionedByEmploymentAgency ;
    		sh:in (true) ;
    	]
        [
            sh:path ff:ruleExemptionBecause ;
            sh:qualifiedValueShape ff:DisabilityShape ;
        ]
    ) .
