    sh:targetNode ff:User ;
    # User has a disability
    sh:property [
        sh:path ff:hasDisabilityType ;
        sh:qualifiedMinCount 1 ;
    ] ;
    # User's rehabilitation provider is the Federal Employment Agency
    sh:property [
        sh:path ff:hasRehabilitationProvider ;
        sh:minCount 1 ;
        sh:in (ff:FederalEmploymentAgency) ;
        sh:message "Does not meet requirement 'User's rehabilitation provider is the Federal Employment Agency'"@en
    ] ;
    # User participates in a training that is either vocational training, vocational preparation, or individual occupational qualification in the context of "supported employment", or a measure in the entry procedure or training area of a workshop for people with disabilities, or a professional further education
    sh:property [
        sh:path ff:participates ;
        sh:qualifiedMinCount 1 ;
        sh:qualifiedValueShape ff:TrainingShape ;
    ] ;
    # The user was employed or self-employed for at least 12 months within the last 3 years OR they have a claim to unemployment benefit according to SGB III and have currently applied for it OR they are entitled to unemployment assistance according to the Soldiers' Welfare Act (SVG)
    sh:or (
        [
            sh:property [
                sh:path [ sh:inversePath ff:hasEmployment ] ;
                sh:qualifiedMinCount 1 ;
                sh:qualifiedValueShape ff:EmploymentShape ;
            ]
        ]
        [
