        [
            sh:property [
                sh:path [ sh:inversePath ff:receivesBenefit ] ;
                sh:qualifiedMinCount 1 ;
                sh:qualifiedValueShape ff:UnemploymentBenefitsSVGShape ;
            ]
        ]
    ) ;
    # OR the user is a vocational returnee with a disability
    sh:or (
        [
            sh:property [
                sh:path ff:isVocationalReturnee ;
