                sh:path [ sh:inversePath ff:hasEvent ] ;
                sh:qualifiedMinCount 1 ;
                sh:qualifiedValueShape ff:AcquiredQualificationShape ;
            ]
        ]
        [
            sh:property [
                sh:path ff:participates ;
                sh:qualifiedMinCount 1 ;
